function createStatePolygon(stateName, returnGeom) {
	var geomAry = new Array(); 
	geomAry = returnGeom.split('|'); 
	var XY = new Array(); 
	var points = []; 
	for (var i = 0; i < geomAry.length; i++) { 
		XY = geomAry[i].split(','); 
		points.push( new google.maps.LatLng(parseFloat(XY[1]),parseFloat(XY[0]))) ; 
	}

	var polygon = new google.maps.Polygon({
		paths: points,
		strokeColor: "#FF0000",
		strokeOpacity: 0.8,
		strokeWeight: 1,
		fillColor: "#FF0000",
		fillOpacity: 0.35,
		stateName: stateName,
		selectedState: false
	});
	
	polygons.push(polygon);
	polygon.setMap(map);

	google.maps.event.addListener(polygon, 'mouseover', function(evt){
		if (!this.selectedState) {
			this.setOptions({fillColor: "#0000FF"});
		}
	});

	google.maps.event.addListener(polygon, 'mouseout', function(evt){
		if (!this.selectedState) {
			this.setOptions({fillColor: "#FF0000"});
		}
	});

	google.maps.event.addListener(polygon, 'click', function(evt){
		selectState(this);
		for ( var i = 0; i < polygons.length; i++) {
			var polygon = polygons[i];
			polygon.setOptions({fillColor: "#FF0000", selectedState: false});
		}
		this.setOptions({fillColor: "#00FF00", selectedState: true});
	});
}

function selectState(polygon) {
	$('#selectedState').val(polygon.stateName);
	$('#selectedStateText').html("Ha seleccionado: " + polygon.stateName);
}